import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.Bw-5EFTY.js";const b=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"plo/short-vs-deep.md","filePath":"plo/short-vs-deep.md"}'),r={name:"plo/short-vs-deep.md"};function o(h,l,u,n,s,d){return e(),a("div",null,l[0]||(l[0]=[t('<h2 id="现场-plo-短码-vs-深码买入策略" tabindex="-1">现场 PLO：短码 vs 深码买入策略 <a class="header-anchor" href="#现场-plo-短码-vs-深码买入策略" aria-label="Permalink to &quot;现场 PLO：短码 vs 深码买入策略&quot;">​</a></h2><h3 id="一、定义" tabindex="-1">一、定义 <a class="header-anchor" href="#一、定义" aria-label="Permalink to &quot;一、定义&quot;">​</a></h3><ul><li>短码（Short Stack）：现场游戏中 10–100BB</li><li>深码（Deep Stack）：现场游戏中 ≥ 200BB</li></ul><p>现场常无上限买入，远深于线上标准。</p><h3 id="二、选择短码或深码前需考虑的因素" tabindex="-1">二、选择短码或深码前需考虑的因素 <a class="header-anchor" href="#二、选择短码或深码前需考虑的因素" aria-label="Permalink to &quot;二、选择短码或深码前需考虑的因素&quot;">​</a></h3><ol><li>牌局分析 <ul><li>游戏是否熟悉？</li><li>抽水规则、是否有边注（如双公共牌炸弹彩池）、是否频繁盲抓</li><li>桌面动态：松 / 紧、激进 / 被动</li><li>左右座位是谁？筹码量分布？</li></ul></li><li>资金与波动 <ul><li>是否能承受波动？</li><li>是否有当日预算 / 止损？</li><li>剩余游戏时间是否值得加深码？</li></ul></li><li>心态 <ul><li>当前局是否在舒适区 / 成长区 / 危险区？</li><li>输赢是否会影响决策质量？</li><li>深码错误是否会造成过大情绪波动？</li></ul></li><li>技术与胜率 <ul><li>自己在桌上的相对水平？</li><li>鱼玩家的水平如何？</li><li>哪种筹码量下 EV、时薪最高？</li></ul></li></ol><h3 id="三、短码策略" tabindex="-1">三、短码策略 <a class="header-anchor" href="#三、短码策略" aria-label="Permalink to &quot;三、短码策略&quot;">​</a></h3><h4 id="优势" tabindex="-1">优势 <a class="header-anchor" href="#优势" aria-label="Permalink to &quot;优势&quot;">​</a></h4><ul><li>决策简单（SPR 小，游戏树浅，减少转牌 / 河牌困难决策）</li><li>易于快速学习 PLO（新手友好）</li><li>权益过度实现（多方底池中可直接在翻牌全押，避免深码后续街被迫放弃）</li><li>方差较小（50BB 方差 ≈ 100BB 的一半）</li><li>易于多桌</li><li>心理压力小</li></ul><blockquote><p>方差：统计学里描述随机变量的离散程度，这里用来描述波动大小。</p></blockquote><h4 id="劣势" tabindex="-1">劣势 <a class="header-anchor" href="#劣势" aria-label="Permalink to &quot;劣势&quot;">​</a></h4><ul><li>对手犯错空间小（损失 EV）</li><li>线上深码可退出重进新台，现场不行（翻倍后被迫深码）</li><li>容易惹恼 VIP / 鱼玩家（尤其在私人局）</li><li>无法长期避免深码局面</li></ul><h4 id="适用场景" tabindex="-1">适用场景 <a class="header-anchor" href="#适用场景" aria-label="Permalink to &quot;适用场景&quot;">​</a></h4><ul><li>热身阶段（状态未进入）</li><li>陌生牌局（先观察动态）</li><li>强对手多的牌局（高风险的对抗）</li><li>左手有激进玩家</li><li>资金有限（分多次买入延长游戏时间）</li><li>升级高额牌局试探（减少方差）</li></ul><h3 id="四、深码策略" tabindex="-1">四、深码策略 <a class="header-anchor" href="#四、深码策略" aria-label="Permalink to &quot;四、深码策略&quot;">​</a></h3><h4 id="优势-1" tabindex="-1">优势 <a class="header-anchor" href="#优势-1" aria-label="Permalink to &quot;优势&quot;">​</a></h4><ul><li>放大优势：可迫使弱手在大底池犯大错</li><li>元游戏（Meta Game 有交牌历史的牌局） &amp; 压制：同筹码压制易怂的玩家</li><li>最大化 EV：筹码覆盖所有鱼玩家最大化 EV</li><li>有位置时深码打鱼 EV 更高</li></ul><h4 id="劣势-1" tabindex="-1">劣势 <a class="header-anchor" href="#劣势-1" aria-label="Permalink to &quot;劣势&quot;">​</a></h4><ul><li>决策难度高（SPR 大，转河复杂）</li><li>权益实现率下降</li><li>方差高，需更大资金</li><li>心理压力大</li></ul><h4 id="适用场景-1" tabindex="-1">适用场景 <a class="header-anchor" href="#适用场景-1" aria-label="Permalink to &quot;适用场景&quot;">​</a></h4><ul><li>抽水高（争取更多封顶底池）</li><li>对手弱（筹码覆盖对手）</li><li>自己是桌上顶尖且左手无压制威胁</li><li>对鱼玩家有位置时</li></ul><h3 id="五、座位选择-短码-深码通用" tabindex="-1">五、座位选择（短码 &amp; 深码通用） <a class="header-anchor" href="#五、座位选择-短码-深码通用" aria-label="Permalink to &quot;五、座位选择（短码 &amp; 深码通用）&quot;">​</a></h3><ul><li>核心原则：钱流向左边 → 要坐在鱼玩家左手（有位置优势）</li><li>优先让最深、最松的玩家在你右手</li><li>左手最好是紧且短码的玩家</li><li>公开局可适时换座，私人局需顾及关系</li></ul><h3 id="六、要点总结" tabindex="-1">六、要点总结 <a class="header-anchor" href="#六、要点总结" aria-label="Permalink to &quot;六、要点总结&quot;">​</a></h3><ul><li>短码：低风险、低方差、降低对手犯错空间</li><li>深码：高风险、高方差、最大化弱手错误的价值</li><li>没有绝对好坏，需根据牌局动态 + 自身资金 + 心理状态 + 技术水平灵活调整。</li></ul>',25)]))}const p=i(r,[["render",o]]);export{b as __pageData,p as default};
